import axios from 'axios';


const apiUrl = "http://localhost:5121"


export default {


  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)
    console.log(result.data);
    return result.data;
  },

  addTask: async (name, id) => {
    // try {
      
    //   var newItem = { id: id, name: name, isComplete: false }
    //   const result = await axios.post(`${apiUrl}/items`, newItem);
    //   console.log('addTask', name)
    //   return result.data;
    // }
    // catch (err) {
    //   console.error('Error adding task:', err);
    //   throw err;
    // }
    try {
      console.log("Adding task:", name);
      const newItem = { id: id, name: name, isComplete: false }; // Constructing the task object
      const result = await axios.post(`${apiUrl}/items`, newItem); // Sending POST request with the task data
      return result.data; // Returning the data from the response, if needed
    } catch (err) {
      console.error('Error adding task:', err);
      throw err; // Rethrow the error for further handling
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      
      const updatedItem = { isComplete: isComplete }; // Constructing the updated task object
      const result = await axios.put(`${apiUrl}/items/${id}`, updatedItem); // Sending PUT request with the updated task dataconsole.log('Setting completion status for task:', id, isComplete);
      return result.data; // Returning the data from the response, if needed
    } catch (err) {
      console.error('Error setting completion status for task:', err);
      throw err; // Rethrow the error for further handling
    }
  },

  deleteTask: async (id) => {
    
    try {
      const result = await axios.delete(`${apiUrl}/items/${id}`);
      console.log('deleteTask');
      return result.data; // You may or may not return data after successful deletion
    } catch (err) {
      console.error('Error deleting task:', err);
      throw err;
    }  }
};
