import React, { useEffect, useState } from 'react';
import service from './service.js';

function App() {
  const [newTodo, setNewTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const [Id, setId]=useState(2);


  async function getTodos() {
    const todos = await service.getTasks();
    setTodos(todos);
  }

  async function createTodo(e) {
    e.preventDefault();
    await service.addTask(newTodo,Id);
    setId(prevId => prevId + 1);
    setNewTodo("");//clear input
    await getTodos();//refresh tasks list (in order to see the new one)
  }

  async function updateCompleted(todo, isComplete) {
    await service.setCompleted(todo.id, isComplete);
    await getTodos();//refresh tasks list (in order to see the updated one)
  }

  async function deleteTodo(id) {
    await service.deleteTask(id);
    await getTodos();//refresh tasks list
  }

  useEffect(() => {
    getTodos();
  }, []);

  const [editingTodoId, setEditingTodoId] = useState(null); // Track which task is being edited
  const [editedTodoName, setEditedTodoName] = useState(""); // Track edited task name

  const handleEdit = (todo) => {
    setEditingTodoId(todo.id);
    setEditedTodoName(todo.name);
  };

  const handleSaveEdit = async (todo) => {
    await service.setCompleted(todo.id, editedTodoName,);
    setEditingTodoId(null);
    await getTodos();
  };

  return (
    <section className="todoapp">
      <header className="header">
        <h1>todos</h1>
        <form onSubmit={createTodo}>
          <input className="new-todo" placeholder="Well, let's take on the day" value={newTodo} onChange={(e) => setNewTodo(e.target.value)} />
        </form>
      </header>
      <section className="main" style={{ display: "block" }}>
        <ul className="todo-list">
          {todos.map(todo => {
            return (
              <li className={todo.isComplete ? "completed" : ""} key={todo.id}>
                <div className="view">
                  <input className="toggle" type="checkbox" defaultChecked={todo.isComplete} onChange={(e) => updateCompleted(todo, e.target.checked)} />
                  <label onDoubleClick={() => handleEdit(todo)}>{editingTodoId === todo.id ? (
                    <input 
                      className="edit" 
                      value={editedTodoName} 
                      onChange={(e) => setEditedTodoName(e.target.value)} 
                      onBlur={() => handleSaveEdit(todo)}
                      autoFocus
                    />
                  ) : (
                    todo.name
                  )}</label>
                  <button className="destroy" onClick={() => deleteTodo(todo.id)}></button>
                </div>
              </li>
            );
          })}
        </ul>
      </section>
    </section >
  );
}

export default App;
